SELECT product_color.product_id, products.name, products.price, tag.tag_name, main_type.mt_name, style.st_name, material.ma_name, product_color.ph1, product_color.ph2, product_color.ph3, product_color.ph4, product_color.ph5, product_color.phs, product_color.color_id, color.color_name, color.color
FROM products JOIN tag ON products.tag_id = tag.id JOIN main_type ON products.mt_id = main_type.id JOIN style ON products.st_id = style.id JOIN material ON products.ma_id = material.id JOIN product_color ON product_color.product_id = products.id JOIN color ON color.id = product_color.color_id;